import React, { Component } from 'react';
import PouchDB from 'pouchdb-browser'
import { connect } from 'react-redux'

import {db, sync, remoteDb} from './DataBase'



class Orders extends Component {
  constructor(props) {
     super(props);
     this.state = {
        data: [],
        loading: true
     };

  }

  //to remove the order from db
  handleRemove = (doc)=>{
    db.remove(doc).then(function (result) {
      //result
      PouchDB.sync(db,remoteDb)

      this.forceUpdate();
    }).catch( function (err) {
      console.log(err);

    });

  }

  componentWillUnmount() {
    this.canceler.cancel();
  }

  componentDidMount() {

    this.fetchData();
    db.sync(remoteDb, {live: true}).on('complete', () => {

      this.fetchData();
    }).on('error', function (err) {
      //if err
      console.log(err);
    });
    this.canceler = db.changes({
       since: 'now',
       live: true,
       include_docs: true,
     }).on('change', () => {
         this.fetchData();
     });
  }

  fetchData = () => {
      this.setState({
          loading: true,
          data: null,
      });
      db.allDocs({
          include_docs: true,
      }).then(result => {
        // handle result
        let dataList = [];
        for (var item in result.rows) {
          dataList.push(result.rows[item]);
        }
        this.setState({ data: dataList, loading: false });

      }).catch((err) =>{
          console.log(err);
      });
  }


  render() {

    if (this.state.loading || this.state.elements === null) {
           return (<p></p>);
    }


    let orderList = this.state.data.map(order => {
      var orderName = order.doc.name;


      let items = order.doc.items.map(item =>{
        return (
          <div>
            <p>Item: {item.title}</p>
            <p>Quantity: {item.quantity}</p>
            <p>SKU: {item.sku}</p>
          </div>


        );

      });

      return (
        <ul class="collection">
          <li class = "collection-item">
              <div>
                <b>Name: {orderName}</b>
              </div>
              <div id="div" class="row center">
                <button className="waves-effect waves-light btn pink remove" onClick={()=>{this.handleRemove(order.doc)}}>Remove</button>
              </div>


          </li>
          <li class = "collection-item">
            <div>{items}</div>
          </li>
        </ul>
      );
    });
    return (

          <ul>
              {orderList}
          </ul>

    )
  }
}

