import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity} from './actions/cartActions'
import Recipe from './Recipe'

function makeArr(items) {
  let arr = [];
  let x =0;
  for (let i = 0; i < items.length; i++) {
    for (let a = 0; a < items[i].quantity; a++ ) {
      arr[x] = items[i].sku;
      x++;
    }


  }
  return arr;

}

class SKUPage extends Component {
  render() {
      let array = makeArr(this.props.addedItems)


    return(
      <div>

        <div>
          <ul class="collection with-header">
            <li class="collection-header"><h4>SKUs</h4></li>
            {array.map(function(item) {
              return <li key={item}>{item}</li>;
            })}

          </ul>
        </div>

      </div>

    )

  }
}

const mapStateToProps = (state)=>{
    return{
        //items: state.addedItems,
        //addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        //removeItem: (id)=>{dispatch(removeItem(id))},
        // addQuantity: (id)=>{dispatch(addQuantity(id))},
        // subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(SKUPage)
