import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import SKUPage from './SKUPage'


class Recipe extends Component{
  constructor(props) {
    super(props);
    this.state = {
      addedItems: [{sku: 10}]
    }
  }

handleClick = (addedItems)=> {
  alert(this.state.addedItems[0].sku)
  this.setState({addedItems})
  alert(this.state.addedItems[0].sku)
}




    render(){

        return(
            <div className="container">
                <div className="collection">

                        <li className="collection-item"><b>Total: ${this.props.total} </b></li>
                    </div>
                    <div></div>

                    <div className="checkout">

                      <Link to="/skupage"><i className="waves-effect waves-light btn">Get SKUs</i></Link>

                    </div>
                    <div className="checkout">

                      <i className="waves-effect waves-light btn" onClick={()=>{this.handleClick(this.props.addedItems)}}>TEST</i>
                      <Link to="/SKUPage">Go to Results</Link>

                    </div>

                 </div>
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        addedItems: state.addedItems,
        total: state.total
    }
}

const mapDispatchToProps = (dispatch)=>{


}


export default connect(mapStateToProps,mapDispatchToProps)(Recipe)
