import React, { Component } from 'react';
import PouchDB from 'pouchdb-browser'
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'
import { showOrders } from './actions/cartActions'
import {db} from './DataBase'



class Orders extends Component {
  constructor(props) {
     super(props);
     this.state = {
        data: []
     }
  }



  componentDidMount() {
    let curr = this;
    db.allDocs({
      include_docs: true,
      attachments: true
    }).then(function (result) {
      // handle result
        alert(result.rows[0].doc._id)
        let dataList = [];
        for (var item in result.rows) {
          result[item].doc._id
          dataList.push(result[item]);
        }
        curr.setState({ data: dataList });


    }).catch(function (err) {
      console.log(err);
    });
  }


  render() {

    let orderList = this.state.data.map(function(order) {
        return <li key={order.doc._id}>{order.doc.name}</li>;
    });
    return (
        <ul>
            {orderList}
        </ul>
    )
  }
}




const mapStateToProps = (state)=>{
    return{


    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
      showOrders: ()=>{dispatch(showOrders())}

    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Orders)
