import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'

 class Home extends Component{

    handleClick = (id)=>{
        this.props.addToCart(id);
        this.setState({state: this.state});

    }

    render(){

        let itemList = this.props.items.map(item=>{
            return(
                <div className="card" key={item.id}>
                        <div className="card-image">
                            <img src={item.img} alt={item.title}/>
                            <span className="card-title">{item.title}</span>
                            {
                              ((item.stock) > 0 && !(item.quantity === item.stock))
                                ? <span to="/" className="btn-floating halfway-fab waves-effect waves-light red" onClick={()=>{this.handleClick(item.id)}}><i className="material-icons">add</i></span>
                                :<span to="/" className="btn-floating halfway-fab waves-effect waves-light disabled"><i class="material-icons">add</i></span>
                            }

                        </div>

                        <div className="card-content">
                            <p>{item.desc}</p>
                            <p><b>Price: ${item.price}</b></p>

                            {
                              (item.stock > 0)
                                ? <p>Stock: {item.stock} </p>
                                : <p className = 'materialize-red-text'>This item is out of stock, but you can order online HERE</p>
                            }


                        </div>
                 </div>

            )
        })

        return(
            <div className="container">
                <h3 className="center">Available Items</h3>
                <div className="box">
                    {itemList}
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state)=>{
    return {
      items: state.items
    }
  }
const mapDispatchToProps= (dispatch)=>{

    return{
        addToCart: (id)=>{dispatch(addToCart(id))},

    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Home)
