import PouchDB from 'pouchdb-browser'



export var db = new PouchDB('orders');
var remoteCouch = 'http://sil:t00lb0x@172.0.0.1/orders';

function sync() {
  syncDom.setAttribute('data-sync-state', 'syncing');
  var opts = {live: true};
  db.replicate.to(remoteCouch, opts, syncError);
  db.replicate.from(remoteCouch, opts, syncError);
}


export function addOrderToDB(order) {

  order._id = new Date().toISOString();


  db.put(order, function callback(err, result) {
    if (!err) {
      console.log('Successfully posted an order!');
    }
  });
}
