var PouchDB = require('pouchdb');


export var db = new PouchDB('orders');
var remoteCouch = false;

db.changes({
  since: 'now',
  live: true
}).on('change', showOrdersFromDB);


export function addOrderToDB(order) {

  order._id = new Date().toISOString();

  db.put(order, function callback(err, result) {
    if (!err) {
      console.log('Successfully posted an order!');
    }
  });
}

export function showOrdersFromDB() {
  db.allDocs({include_docs: true, descending: true}, function(err, doc) {
    alert("showing orders");
  });
}

export function testing() {
  alert("testing");
}
