import React, { Component } from 'react';
import PouchDB from 'pouchdb-browser'
import { connect } from 'react-redux'

import {db} from './DataBase'





class Orders extends Component {
  constructor(props) {
     super(props);
     this.state = {
        data: []
     }
  }



  componentDidMount() {
    let curr = this;
    db.allDocs({
      include_docs: true,
      attachments: true
    }).then(function (result) {
      // handle result

        let dataList = [];
        for (var item in result.rows) {
          dataList.push(result.rows[item]);
        }
        curr.setState({ data: dataList });



    }).catch(function (err) {
      console.log(err);
    });
  }


  render() {


    let orderList = this.state.data.map(function(order) {
      let items = order.doc.items.map(function(item) {
        return (
          <div>
            <p>{item.title}</p>
            <p>    {item.quantity}</p>
            <p>    {item.sku}</p>
          </div>




        );

      });

      return (
        <ul class="collection">
          <li class = "collection-item">

              <div><b>{order.doc.name}</b><a href="#!" class="secondary-content"><i class="material-icons">send</i></a></div>

          </li>
          <li class = "collection-item">
            <div>{items}</div>
          </li>
        </ul>
      );
    });
    return (
        <ul>
            {orderList}
        </ul>
    )
  }
}




