import PouchDB from 'pouchdb-browser'


export var db = new PouchDB('orders');
export var remoteDb = 'https://b9311cbe-6ddd-43a5-ac90-faf84cdac39b-bluemix:6cb9b03ed3b6023a8d5b423247a62bdcdeae959ab809fc1cf6bfeccc658ac1f3@b9311cbe-6ddd-43a5-ac90-faf84cdac39b-bluemix.cloudantnosqldb.appdomain.cloud/orders';
export function sync() {
  var opts = {live: true};
  db.replicate.to(remoteDb, opts);
  db.replicate.from(remoteDb, opts);
}


export function addOrderToDB(order) {
  var  date = new Date()
  order._id = date.toISOString();

   order.time = date.getHours() +':'+ date.getMinutes();


  db.put(order, function callback(err, result) {
    if (!err) {
      console.log('Successfully posted an order!');
    }

    db.sync(remoteDb, {live: true})
  });
}
