import data from '../items.json'
import { ADD_TO_CART,REMOVE_ITEM,SUB_QUANTITY,ADD_QUANTITY, ADD_ORDER, SHOW_ORDERS} from '../actions/action-types/cart-actions'
import PouchDB from 'pouchdb-browser'


var db = new PouchDB('orders');
var remoteCouch = false;

var importedItems = require('../items.json'); //(with path)


db.changes({
  since: 'now',
  live: true
}).on('change', showAllOrders);

function addOrder(order){
  order._id  = new Date().toISOString()

  db.put(order, function callback(err, result) {
    if (!err) {
      console.log('Successfully posted an order!');
    }
  });
}

function showAllOrders() {

  return db.allDocs({include_docs: true, descending: true}, function(err, doc) {
    return doc
  });
}



const initState = {

    items: importedItems.items,
    orders: importedItems.orders,

    addedItems:[],
    total: 0

}
const cartReducer= (state = initState,action)=>{
  //alert(initState.items[0].title)

    //INSIDE HOME COMPONENT
    if(action.type === ADD_TO_CART){

          let addedItem = state.items.find(item=> item.id === action.id)
          //check if the action id exists in the addedItems
    let existed_item = state.addedItems.find(item=> action.id === item.id)
         var editedItems = state.items
         var idx = editedItems.indexOf(addedItem)


         if(existed_item)
         {
            addedItem.quantity += 1

            editedItems[idx] = addedItem
             return{
                ...state,
                 total: state.total + addedItem.price,
                 items: editedItems

                  }
        }
         else{
            addedItem.quantity = 1;
            //calculating the total
            let newTotal = state.total + addedItem.price

            return{
                ...state,
                addedItems: [...state.addedItems, addedItem],
                total : newTotal
            }

        }
    }
    if(action.type === REMOVE_ITEM){
        let itemToRemove= state.addedItems.find(item=> action.id === item.id)
        let new_items = state.addedItems.filter(item=> action.id !== item.id)

        //calculating the total
        let newTotal = state.total - (itemToRemove.price * itemToRemove.quantity )
        console.log(itemToRemove)
        return{
            ...state,
            addedItems: new_items,
            total: newTotal
        }
    }
    //INSIDE CART COMPONENT
    if(action.type=== ADD_QUANTITY){
        let addedItem = state.items.find(item=> item.id === action.id)
          addedItem.quantity += 1
          let newTotal = state.total + addedItem.price
          return{
              ...state,
              total: newTotal
          }
    }







    if(action.type=== SUB_QUANTITY){
        let addedItem = state.items.find(item=> item.id === action.id)
        //if the qt == 0 then it should be removed
        if(addedItem.quantity === 1){
            let new_items = state.addedItems.filter(item=>item.id !== action.id)
            let newTotal = state.total - addedItem.price
            return{
                ...state,
                addedItems: new_items,
                total: newTotal
            }
        }
        else {
            addedItem.quantity -= 1
            let newTotal = state.total - addedItem.price
            return{
                ...state,
                total: newTotal
            }
        }

    }


    if(action.type=== ADD_ORDER){

      let items = state.addedItems
      let newOrders = state.orders
      let newOrder = {name: action.name, items: items}
      addOrder(newOrder)
      newOrders.push(newOrder)


      return{
          ...state,
          orders: newOrders
      }

    }

    if(action.type === SHOW_ORDERS){
        //alert("hi")
        var orders = showAllOrders();
        setTimeout(alert("waited"), 1000);
        console.log(orders)
        alert(orders.rows.length)


        return orders;
    }





  else{
    return state
    }

}

export default cartReducer
