import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'

const sqlite3 = require('sqlite3').verbose();

// open the database
let db = new sqlite3.Database('./db/sample.db', sqlite3.OPEN_READWRITE, (err) => {
  if (err) {
    console.error(err.message);
  }
  console.log('Connected to the sample database.');
});

db.serialize(() => {
  db.each(`SELECT PlaylistId as id,
                  Name as name
           FROM playlists`, (err, row) => {
    if (err) {
      console.error(err.message);
    }
    console.log(row.id + "\t" + row.name);
  });
});

db.close((err) => {
  if (err) {
    console.error(err.message);
  }
  console.log('Close the database connection.');
});



class Orders extends Component {

  render() {
    let orderList = this.props.orders.map(order=>{
      let itemList = order.items.map(item=>{
        return(
          <ul>
            <li>{item.title}</li>
            <ul>
              <li>{item.quantity}</li>
              <li>{item.sku}</li>
            </ul>

          </ul>
        )

      })
      return(
        <div class = "container" key={order.name}>
          <p>Name: {order.name}</p>
          <div>
            {itemList}
          </div>


        </div>
      )
    })

    return (
      <div class = "container">
        <h3>Orders</h3>
        <div className="box">
            {orderList}
        </div>
      </div>

    )
  }
}




const mapStateToProps = (state)=>{
    return{
      items: state.items,
      orders: state.orders

    }
}
const mapDispatchToProps = (dispatch)=>{
    return{

    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Orders)
