import React, { Component } from 'react';
import PouchDB from 'pouchdb-browser'
import { connect } from 'react-redux'

import {db} from './DataBase'



class Orders extends Component {
  constructor(props) {
     super(props);
     this.state = {
        data: []
     }
  }



  componentDidMount() {
    let curr = this;
    db.allDocs({
      include_docs: true,
      attachments: true
    }).then(function (result) {
      // handle result

        let dataList = [];
        for (var item in result.rows) {
          dataList.push(result.rows[item]);
        }
        curr.setState({ data: dataList });


    }).catch(function (err) {
      console.log(err);
    });
  }


  render() {

    let orderList = this.state.data.map(function(order) {
      items = order.doc.items.map(function(item) {
        return (
          <ul>
            <li class = "collection-item">{item.title}</li>
            <li class = "collection-item">{item.quantity}</li>
            <li class = "collection-item">{item.sku}</li>
          </ul>

        );

      });

      return (
        <ul class="collection">
          <li class = "collection-item"><b>{order.doc.name}</b></li>
          {items}

        </ul>
      );
    });
    return (
        <ul>
            {orderList}
        </ul>
    )
  }
}




