import PouchDB from 'pouchdb-browser'



export var db = new PouchDB('orders');
export var remoteDb = 'https://49da27c9-c63e-40af-9e56-3dc5802011cc-bluemix.cloudantnosqldb.appdomain.cloud/orders';

export function sync() {
  var opts = {live: true};
  db.replicate.to(remoteDb, opts);
  db.replicate.from(remoteDb, opts);
}


export function addOrderToDB(order) {

  order._id = new Date().toISOString();


  db.put(order, function callback(err, result) {
    if (!err) {
      console.log('Successfully posted an order!');
    }
    PouchDB.sync(db,remoteDb)
  });
}
