import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import SKUList from './SKUList'
import Orders from './Orders'


class Recipe extends Component{
  constructor(props) {
    super(props);
    this.state = {orders:[]};
  }


handleClick(addedItems) {
  const test = "hello";
}

handleSubmit = (id)=>{
    var name = document.getElementById("nameInput").value
    this.state.orders.push({name: name, items: this.props.addedItems })
    alert(this.state.orders.length)
    // alert(document.getElementById("nameInput").value);

}



    render(){

        return(
            <div className="container">
                <div className="collection">

                        <li className="collection-item"><b>Total: ${this.props.total} </b></li>
                    </div>
                    <div></div>

                    <div className="checkout">



                    </div>


                    <div class="row">
                      <form class="col s12">
                        <div class="row">
                          <div class="input-field col s12">
                            <input id="nameInput" type="text" class="validate"></input>
                            <label for="nameInput" class="active">Name</label>
                          </div>
                        </div>
                      </form>
                    </div>


                    <div>
                    <Link to={{ pathname: '/OrderResponse', state: {name: ""}}}>
                      <button class="btn waves-effect waves-light" type="submit" name="action" onClick={()=>{this.handleSubmit()}}>Submit
                        <i class="material-icons right">send</i>
                      </button>
                    </Link>
                    </div>


                    <div>
                      {
                        (this.props.addedItems.length === 0)
                          ? <p>no items in cart</p>
                          :<SKUList addedItems={this.props.addedItems} />
                      }

                    </div>

                 </div>
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        addedItems: state.addedItems,
        total: state.total,
        orders: state.orders
    }
}

const mapDispatchToProps = (dispatch)=>{


}


export default connect(mapStateToProps,mapDispatchToProps)(Recipe)
