import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
class Recipe extends Component{
  getInputValue = ()=>{
    var customerName = document.getElementById("nameField").value;
    alert("Thanks. Your order will be ready for pick up from Customer Service");
  }





    render(){

        return(
            <div className="container">
                <div className="collection">

                        <li className="collection-item"><b>Total: ${this.props.total} </b></li>
                    </div>
                    <div></div>

                    <div class = "input-field col s6">
                      <input placeholder = "Enter Your Name" id = "nameField" type = "text" class = "validate">
                      </input>

                    </div>

                    <div className="checkout">

                      <button className="waves-effect waves-light btn" onClick = {() => this.getInputValue()}>Order</button>
                      <li><Link to="/"><i className="waves-effect waves-light btn">Get SKUs</i></Link></li>

                    </div>

                 </div>
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        addedItems: state.addedItems,
        total: state.total
    }
}

const mapDispatchToProps = (dispatch)=>{

}


export default connect(mapStateToProps,mapDispatchToProps)(Recipe)
